module.install:
DROP TABLE IF EXISTS `chunks`;
-- command split --
CREATE TABLE `chunks` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(60) NOT NULL, `slug` varchar(60) NOT NULL, `type` varchar(40) NOT NULL, `content` text, `when_added` datetime DEFAULT NULL, `last_updated` datetime DEFAULT NULL, `added_by` int(11) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
